import { type User, type InsertUser } from "@shared/schema";
import { randomUUID } from "crypto";

// modify the interface with any CRUD methods
// you might need

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;

  constructor() {
    this.users = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.email === email,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      id,
      status: 'active',
      createdAt: new Date().toISOString()
    };
    this.users.set(id, user);
    return user;
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { z } from "zod";
import { createInsertSchema } from "drizzle-zod";

// User schema
export const insertUserSchema = z.object({
  name: z.string().min(1, "Name is required"),
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  role: z.string().min(1, "Role is required"),
});

export const loginSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(1, "Password is required"),
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type LoginUser = z.infer<typeof loginSchema>;

export interface User {
  id: string;
  name: string;
  email: string;
  role: string;
  status: 'active' | 'inactive';
  lastLogin?: string;
  createdAt: string;
}

// Role schema
export const insertRoleSchema = z.object({
  name: z.string().min(1, "Role name is required"),
  description: z.string().optional(),
  permissions: z.array(z.string()).default([]),
  color: z.string().default("primary"),
});

export type InsertRole = z.infer<typeof insertRoleSchema>;

export interface Role {
  id: string;
  name: string;
  description?: string;
  permissions: string[];
  color: string;
  userCount: number;
  createdAt: string;
}

// Permission types
export type Permission = 
  | 'dashboard:read'
  | 'users:read' | 'users:create' | 'users:update' | 'users:delete'
  | 'roles:read' | 'roles:create' | 'roles:update' | 'roles:delete'
  | 'reports:read' | 'reports:export'
  | 'settings:read' | 'settings:update'
  | 'billing:read' | 'billing:update';

// Activity log
export interface ActivityLog {
  id: string;
  userId: string;
  userName: string;
  action: string;
  description: string;
  timestamp: string;
}

client/src/App.tsx.content:
import { Switch, Route, Redirect } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { AuthProvider, useAuth } from "@/hooks/use-auth";
import { ThemeProvider } from "@/hooks/use-theme";
import { useState } from "react";

// Pages
import Login from "@/pages/login";
import Dashboard from "@/pages/dashboard";
import Users from "@/pages/users";
import Roles from "@/pages/roles";
import Reports from "@/pages/reports";
import Settings from "@/pages/settings";
import Billing from "@/pages/billing";
import NoAccess from "@/pages/no-access";
import NotFound from "@/pages/not-found";

// Layout Components
import Sidebar from "@/components/layout/sidebar";
import Header from "@/components/layout/header";
import ProtectedRoute from "@/components/auth/protected-route";

function DashboardLayout({ children, title }: { children: React.ReactNode; title: string }) {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <div className="min-h-screen bg-background">
      <Sidebar 
        isOpen={sidebarOpen} 
        onClose={() => setSidebarOpen(false)} 
      />

      <div className="lg:pl-64">
        <Header 
          onToggleSidebar={() => setSidebarOpen(!sidebarOpen)} 
          title={title}
        />

        <main className="p-6" data-testid="main-content">
          {children}
        </main>
      </div>
    </div>
  );
}

function AuthenticatedRoutes() {
  return (
    <Switch>
      <Route path="/dashboard">
        <DashboardLayout title="Dashboard">
          <ProtectedRoute requiredPermission="dashboard:read">
            <Dashboard />
          </ProtectedRoute>
        </DashboardLayout>
      </Route>

      <Route path="/users">
        <DashboardLayout title="User Management">
          <ProtectedRoute requiredPermission="users:read">
            <Users />
          </ProtectedRoute>
        </DashboardLayout>
      </Route>

      <Route path="/roles">
        <DashboardLayout title="Role Management">
          <ProtectedRoute requiredPermission="roles:read">
            <Roles />
          </ProtectedRoute>
        </DashboardLayout>
      </Route>

      <Route path="/reports">
        <DashboardLayout title="Reports">
          <ProtectedRoute requiredPermission="reports:read">
            <Reports />
          </ProtectedRoute>
        </DashboardLayout>
      </Route>

      <Route path="/settings">
        <DashboardLayout title="Settings">
          <ProtectedRoute requiredPermission="settings:read">
            <Settings />
          </ProtectedRoute>
        </DashboardLayout>
      </Route>

      <Route path="/billing">
        <DashboardLayout title="Billing">
          <ProtectedRoute requiredPermission="billing:read">
            <Billing />
          </ProtectedRoute>
        </DashboardLayout>
      </Route>

      <Route path="/no-access">
        <DashboardLayout title="Access Denied">
          <NoAccess />
        </DashboardLayout>
      </Route>

      {/* Default redirect to dashboard */}
      <Route path="/">
        <Redirect to="/dashboard" />
      </Route>

      {/* Fallback to 404 */}
      <Route>
        <DashboardLayout title="Page Not Found">
          <NotFound />
        </DashboardLayout>
      </Route>
    </Switch>
  );
}

function Router() {
  const { isAuthenticated, loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <Switch>
      <Route path="/login">
        {isAuthenticated ? <Redirect to="/dashboard" /> : <Login />}
      </Route>

      <Route>
        {!isAuthenticated ? <Redirect to="/login" /> : <AuthenticatedRoutes />}
      </Route>
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <AuthProvider>
          <TooltipProvider>
            <Toaster />
            <Router />
          </TooltipProvider>
        </AuthProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;

