import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { AuthService, AuthUser } from '@/lib/auth';
import { LocalStorage } from '@/lib/storage';
import type { LoginUser } from '@shared/schema';

interface AuthContextType {
  user: AuthUser | null;
  login: (credentials: LoginUser) => Promise<void>;
  quickLogin: (roleType: 'admin' | 'manager' | 'staff') => Promise<void>;
  register: (userData: any) => Promise<void>;
  logout: () => void;
  isAuthenticated: boolean;
  loading: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<AuthUser | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Initialize sample data
    LocalStorage.initializeData();

    // Check for existing session
    const currentUser = AuthService.getCurrentUser();
    setUser(currentUser);
    setLoading(false);
  }, []);

  const login = async (credentials: LoginUser) => {
    try {
      const authUser = AuthService.login(credentials);
      setUser(authUser);
    } catch (error) {
      throw error;
    }
  };

  const quickLogin = async (roleType: 'admin' | 'manager' | 'staff') => {
    try {
      const authUser = AuthService.quickLogin(roleType);
      setUser(authUser);
    } catch (error) {
      throw error;
    }
  };

  const register = async (userData: any) => {
    try {
      const authUser = AuthService.register(userData);
      setUser(authUser);
    } catch (error) {
      throw error;
    }
  };

  const logout = () => {
    AuthService.logout();
    setUser(null);
  };

  const value: AuthContextType = {
    user,
    login,
    quickLogin,
    register,
    logout,
    isAuthenticated: !!user,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

