import { LocalStorage } from './storage';
import type { LoginUser, User } from '@shared/schema';
import { nanoid } from 'nanoid';

export interface AuthUser extends User {
  permissions: string[];
}

export class AuthService {
  static login(credentials: LoginUser): AuthUser | null {
    const users = LocalStorage.getUsers();
    const roles = LocalStorage.getRoles();

    const user = users.find(u => 
      u.email === credentials.email && u.password === credentials.password
    );

    if (!user) {
      throw new Error('Invalid email or password');
    }

    if (user.status !== 'active') {
      throw new Error('Account is deactivated');
    }

    // Get role permissions
    const role = roles.find(r => r.name === user.role);
    const permissions = role?.permissions || [];

    // Update last login
    LocalStorage.updateUser(user.id, { 
      lastLogin: new Date().toISOString() 
    });

    // Log activity using enhanced logging
    LocalStorage.logActivity({
      action: 'login',
      description: `${user.name} logged in`,
      targetId: user.id,
      targetType: 'user',
      actorId: user.id,
      actorName: user.name
    });

    const authUser: AuthUser = {
      ...user,
      permissions
    };

    LocalStorage.saveSession(authUser);
    return authUser;
  }

  static register(userData: any): AuthUser {
    const users = LocalStorage.getUsers();
    const roles = LocalStorage.getRoles();

    // Check if email already exists
    if (users.find(u => u.email === userData.email)) {
      throw new Error('Email already exists');
    }

    const newUser: User = {
      id: nanoid(),
      name: userData.name,
      email: userData.email,
      role: userData.role || 'Staff',
      status: 'active',
      createdAt: new Date().toISOString(),
      lastLogin: new Date().toISOString()
    };

    LocalStorage.addUser(newUser, 'system', 'Self-Registration');

    // Get role permissions
    const role = roles.find(r => r.name === newUser.role);
    const permissions = role?.permissions || [];

    const authUser: AuthUser = {
      ...newUser,
      permissions
    };

    LocalStorage.saveSession(authUser);
    return authUser;
  }

  static logout(): void {
    const session = LocalStorage.getSession();
    if (session) {
      LocalStorage.logActivity({
        action: 'logout',
        description: `${session.name} logged out`,
        targetId: session.id,
        targetType: 'user',
        actorId: session.id,
        actorName: session.name
      });
    }
    LocalStorage.clearSession();
  }

  static getCurrentUser(): AuthUser | null {
    const session = LocalStorage.getSession();
    if (!session) return null;

    // Refresh permissions from current roles
    const roles = LocalStorage.getRoles();
    const role = roles.find(r => r.name === session.role);
    const permissions = role?.permissions || [];

    return {
      ...session,
      permissions
    };
  }

  static quickLogin(roleType: 'admin' | 'manager' | 'staff'): AuthUser {
    const roleMap = {
      admin: { email: 'admin@example.com', password: 'admin123' },
      manager: { email: 'manager@example.com', password: 'manager123' },
      staff: { email: 'staff@example.com', password: 'staff123' }
    };

    const result = this.login(roleMap[roleType]);
    if (!result) {
      throw new Error('Quick login failed');
    }
    return result;
  }
}

