// localStorage utilities for RBAC system
export class LocalStorage {
  private static readonly USERS_KEY = 'rbac-users';
  private static readonly ROLES_KEY = 'rbac-roles';
  private static readonly SESSION_KEY = 'rbac-session';
  private static readonly ACTIVITY_KEY = 'rbac-activity';

  // Users
  static getUsers(): any[] {
    return JSON.parse(localStorage.getItem(this.USERS_KEY) || '[]');
  }

  static saveUsers(users: any[]): void {
    localStorage.setItem(this.USERS_KEY, JSON.stringify(users));
  }

  static addUser(user: any, actorId?: string, actorName?: string): void {
    const users = this.getUsers();
    users.push(user);
    this.saveUsers(users);

    // Log activity
    this.logActivity({
      action: 'user_created',
      description: `User ${user.name} (${user.email}) was created with ${user.role} role`,
      targetId: user.id,
      targetType: 'user',
      actorId: actorId || 'system',
      actorName: actorName || 'System'
    });
  }

  static updateUser(id: string, updates: any, actorId?: string, actorName?: string): void {
    const users = this.getUsers();
    const index = users.findIndex(u => u.id === id);
    if (index !== -1) {
      const oldUser = { ...users[index] };
      users[index] = { ...users[index], ...updates };
      this.saveUsers(users);

      // Log activity with details about what changed
      const changes = Object.keys(updates).map(key => `${key}: ${oldUser[key]} → ${updates[key]}`).join(', ');
      this.logActivity({
        action: 'user_updated',
        description: `User ${oldUser.name} was updated: ${changes}`,
        targetId: id,
        targetType: 'user',
        actorId: actorId || 'system',
        actorName: actorName || 'System'
      });
    }
  }

  static deleteUser(id: string, actorId?: string, actorName?: string): void {
    const users = this.getUsers();
    const userToDelete = users.find(u => u.id === id);
    const filteredUsers = users.filter(u => u.id !== id);
    this.saveUsers(filteredUsers);

    if (userToDelete) {
      this.logActivity({
        action: 'user_deleted',
        description: `User ${userToDelete.name} (${userToDelete.email}) was deleted`,
        targetId: id,
        targetType: 'user',
        actorId: actorId || 'system',
        actorName: actorName || 'System'
      });
    }
  }

  // Roles
  static getRoles(): any[] {
    return JSON.parse(localStorage.getItem(this.ROLES_KEY) || '[]');
  }

  static saveRoles(roles: any[]): void {
    localStorage.setItem(this.ROLES_KEY, JSON.stringify(roles));
  }

  static addRole(role: any, actorId?: string, actorName?: string): void {
    const roles = this.getRoles();
    roles.push(role);
    this.saveRoles(roles);

    // Log activity
    this.logActivity({
      action: 'role_created',
      description: `Role "${role.name}" was created with ${role.permissions.length} permissions`,
      targetId: role.id,
      targetType: 'role',
      actorId: actorId || 'system',
      actorName: actorName || 'System'
    });
  }

  static updateRole(id: string, updates: any, actorId?: string, actorName?: string): void {
    const roles = this.getRoles();
    const index = roles.findIndex(r => r.id === id);
    if (index !== -1) {
      const oldRole = { ...roles[index] };
      roles[index] = { ...roles[index], ...updates };
      this.saveRoles(roles);

      // Log activity with details about what changed
      const changes = Object.keys(updates).map(key => {
        if (key === 'permissions') {
          return `permissions: ${oldRole[key]?.length || 0} → ${updates[key]?.length || 0} permissions`;
        }
        return `${key}: ${oldRole[key]} → ${updates[key]}`;
      }).join(', ');

      this.logActivity({
        action: 'role_updated',
        description: `Role "${oldRole.name}" was updated: ${changes}`,
        targetId: id,
        targetType: 'role',
        actorId: actorId || 'system',
        actorName: actorName || 'System'
      });
    }
  }

  static deleteRole(id: string, actorId?: string, actorName?: string): void {
    const roles = this.getRoles();
    const roleToDelete = roles.find(r => r.id === id);
    const filteredRoles = roles.filter(r => r.id !== id);
    this.saveRoles(filteredRoles);

    if (roleToDelete) {
      this.logActivity({
        action: 'role_deleted',
        description: `Role "${roleToDelete.name}" was deleted`,
        targetId: id,
        targetType: 'role',
        actorId: actorId || 'system',
        actorName: actorName || 'System'
      });
    }
  }

  // Session
  static getSession(): any | null {
    const session = localStorage.getItem(this.SESSION_KEY);
    return session ? JSON.parse(session) : null;
  }

  static saveSession(user: any): void {
    localStorage.setItem(this.SESSION_KEY, JSON.stringify(user));
  }

  static clearSession(): void {
    localStorage.removeItem(this.SESSION_KEY);
  }

  // Activity logs
  static getActivityLogs(): any[] {
    return JSON.parse(localStorage.getItem(this.ACTIVITY_KEY) || '[]');
  }

  static addActivityLog(log: any): void {
    const logs = this.getActivityLogs();
    logs.unshift(log); // Add to beginning
    // Keep only last 100 logs
    if (logs.length > 100) {
      logs.splice(100);
    }
    localStorage.setItem(this.ACTIVITY_KEY, JSON.stringify(logs));
  }

  // Enhanced activity logging with structured data
  static logActivity(activity: {
    action: string;
    description: string;
    targetId?: string;
    targetType?: 'user' | 'role' | 'system';
    actorId: string;
    actorName: string;
    metadata?: any;
  }): void {
    const log = {
      id: this.generateId(),
      ...activity,
      timestamp: new Date().toISOString(),
      metadata: activity.metadata || {}
    };
    this.addActivityLog(log);
  }

  // Helper method to generate unique IDs
  private static generateId(): string {
    return Date.now().toString(36) + Math.random().toString(36).substr(2);
  }

  // Data export utilities
  static exportUsersToCSV(): string {
    const users = this.getUsers();
    const headers = ['ID', 'Name', 'Email', 'Role', 'Status', 'Created At', 'Last Login'];
    const csvContent = [
      headers.join(','),
      ...users.map(user => [
        user.id,
        `"${user.name}"`,
        user.email,
        user.role,
        user.status,
        new Date(user.createdAt).toLocaleDateString(),
        user.lastLogin ? new Date(user.lastLogin).toLocaleDateString() : 'Never'
      ].join(','))
    ].join('\n');
    return csvContent;
  }

  static exportUsersToJSON(): string {
    const users = this.getUsers();
    return JSON.stringify(users, null, 2);
  }

  static exportActivityLogsToCSV(): string {
    const logs = this.getActivityLogs();
    const headers = ['ID', 'Action', 'Description', 'Actor', 'Target Type', 'Target ID', 'Timestamp'];
    const csvContent = [
      headers.join(','),
      ...logs.map(log => [
        log.id,
        log.action,
        `"${log.description}"`,
        `"${log.actorName}"`,
        log.targetType || '',
        log.targetId || '',
        new Date(log.timestamp).toLocaleString()
      ].join(','))
    ].join('\n');
    return csvContent;
  }

  static exportActivityLogsToJSON(): string {
    const logs = this.getActivityLogs();
    return JSON.stringify(logs, null, 2);
  }

  static downloadFile(content: string, filename: string, mimeType: string): void {
    const blob = new Blob([content], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  }

  // Initialize with sample data if empty
  static initializeData(): void {
    if (this.getUsers().length === 0) {
      const sampleUsers = [
        {
          id: '1',
          name: 'John Admin',
          email: 'admin@example.com',
          password: 'admin123',
          role: 'Administrator',
          status: 'active',
          lastLogin: new Date().toISOString(),
          createdAt: new Date().toISOString()
        },
        {
          id: '2',
          name: 'Sarah Manager',
          email: 'manager@example.com',
          password: 'manager123',
          role: 'Manager',
          status: 'active',
          lastLogin: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(),
          createdAt: new Date().toISOString()
        },
        {
          id: '3',
          name: 'Mike Staff',
          email: 'staff@example.com',
          password: 'staff123',
          role: 'Staff',
          status: 'active',
          lastLogin: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
          createdAt: new Date().toISOString()
        }
      ];
      this.saveUsers(sampleUsers);
    }

    if (this.getRoles().length === 0) {
      const sampleRoles = [
        {
          id: '1',
          name: 'Administrator',
          description: 'Full system access with all administrative privileges',
          permissions: [
            'dashboard:read',
            'users:read', 'users:create', 'users:update', 'users:delete',
            'roles:read', 'roles:create', 'roles:update', 'roles:delete',
            'reports:read', 'reports:export',
            'settings:read', 'settings:update',
            'billing:read', 'billing:update'
          ],
          color: 'primary',
          userCount: 1,
          createdAt: new Date().toISOString()
        },
        {
          id: '2',
          name: 'Manager',
          description: 'Manage team members and view reports with limited administrative access',
          permissions: [
            'dashboard:read',
            'reports:read', 'reports:export'
          ],
          color: 'secondary',
          userCount: 1,
          createdAt: new Date().toISOString()
        },
        {
          id: '3',
          name: 'Staff',
          description: 'Basic access for daily operations and personal dashboard viewing',
          permissions: [
            'dashboard:read'
          ],
          color: 'accent',
          userCount: 1,
          createdAt: new Date().toISOString()
        }
      ];
      this.saveRoles(sampleRoles);
    }
  }
}

